import Layout from '../components/layout/Layout';
import FileLoader from '../components/fileLoader/FileLoader';
import Title from '../components/title/Title';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
const Actions = (props) => {
  const { onCancel, onSuccess } = props;
  return (
    <div className='actions__ctn'>
      <Button variant='outline-danger' onClick={onCancel}>
        Cancelar
      </Button>
      <Button variant='outline-secondary' onClick={onSuccess}>
        Guardar
      </Button>
    </div>
  );
};

const CargarArchivo = (props) => {
  const [files, setFiles] = useState(null);

  const saveFile = async () => {
    const formdata = new FormData();
    const [error, setError] = useState(null);
    const [success, setData] = useState(false);
    const [loading, setLoading] = useState(false);

    formdata.append('csv', files);

    const response = await fetch(
      'http://' + process.env.API_HOST + '/schedule',
      {
        method: 'POST',
        headers: {},
        body: formdata,
      }
    );
  };
  return (
    <Layout>
      <Title>Cargar archivo CSV</Title>
      <FileLoader files={files} setFiles={setFiles}></FileLoader>
      {error && (
        <Alert key={'idx'} variant={'danger'}>
          Error al agregar el archivo
        </Alert>
      )}
      {success && (
        <Alert key={'idx'} variant={'success'}>
          Archivo agregado correctamente
        </Alert>
      )}

      {files ? (
        <Actions
          onCancel={() => {
            setFiles([]);
          }}
          onSuccess={saveFile}
        ></Actions>
      ) : null}
    </Layout>
  );
};

export default CargarArchivo;
